mapper.select:
SELECT COUNT(*)
FROM FESTIVAL
WHERE FESTIVAL_NAME = #{festivalName}
 AND FESTIVAL_START_DATE = #{festivalStartDate}

SELECT * FROM (
    SELECT f.*, ROWNUM AS rnum
    FROM (
        SELECT *
        FROM FESTIVAL WHERE FESTIVAL_FILE_PATH IS NOT NULL AND FESTIVAL_END_DATE >=SYSDATE
        ORDER BY FESTIVAL_END_DATE
    ) f
    WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}
